import styled from 'styled-components';

const PrettyDiv = styled.div`
    background-color: lightcyan;
    border: 2px solid black;
    border-radius: 15px;
    padding: 10px;
    margin: 10px;
    box-shadow: 5px 5px 5px 0px lightgreen;
    float: left;
    position: relative;
`;

const Nameh2 = styled.h2`
    background-color: lightblue;
    border-radius: 5px;
    padding: 10px;
    margin: 10px;
    box-shadow: 5px 5px 5px 0px black;
    font-size: 14px
`

const Statush2 = styled.h2`
    background-color: lightgreen;
    border-radius: 5px;
    padding: 10px;
    margin: 10px;
    box-shadow: 5px 5px 5px 0px green;
    font-size: 14px
`

const Speciesh2 = styled.h2`
    background-color: lightyellow;
    border-radius: 5px;
    padding: 10px;
    margin: 10px;
    box-shadow: 5px 5px 5px 0px black;
    font-size: 14px;
    position: relative;
    top: 10;
    left: 0;
    z-index: 1;
`

const Genderh2 = styled.h2`
    background-color: lightyellow;
    border-radius: 5px;
    padding: 10px;
    margin: 10px;
    box-shadow: 5px 5px 5px 0px black;
    font-size: 14px;
    position: relative;
    top: 0;
    left: 10;
    z-index: 1;
`


const Originh2 = styled.h2`
    background-color: lightyellow;
    border-radius: 5px;
    padding: 10px;
    margin: 10px;
    box-shadow: 5px 5px 5px 0px black;
    font-size: 14px;
    position: absolute;
    top: 0;
    left: 0;
    right: 10;
    z-index: 1;
`

const Button = styled.button`
    background-color: red;
    border-radius: 9px;
    border: 1px solid black;
    position: absolute;
    right: 0;
    left: 10;
    top: 0;
    bottom: 20;

`


export { PrettyDiv, Nameh2, Statush2, Speciesh2, Originh2, Genderh2, Button };
