import { ADD_FAV, REMOVE_FAV, FILTER, ORDER } from "./Types";

const initialState = {
  myFavorites: [],
  allCharacters: [],
};

function rootReducer(state = initialState, { type, payload }) {
  switch (type) {
    case ADD_FAV:
      return { ...state, myFavorites: payload, allCharacters: payload };
    case REMOVE_FAV:
      return { ...state, myFavorites: payload };
    case FILTER:
      let copy2 = [...state.allCharacters];
      let genderFilter = copy2.filter((character) => {
        return character.gender === payload;
      });
      return {
        ...state,
        filteredCharacters: genderFilter,
      };
    case ORDER:
      let copy3 = [...state.allCharacters];
      return {
        ...state,
        filteredCharacters: copy3.sort((a, b) => {
          return payload === "A" ? a.id - b.id : b.id - a.id;
        }),
      };
    default:
      return state;
  }
}

export default rootReducer;
