import styled from 'styled-components';

const PrettyDiv = styled.div`
    border: 2px solid black;
    border-radius: 5px;
    padding: 10px;
    margin: 10px;
    box-shadow: 5px 5px 5px 0px lightgreen;
    float: left;
    width: 900px;
    height: 700px;
`;

export default PrettyDiv;
