import styled from 'styled-components';
const cardStyle = styled.div`
   display: flex;
   flex-direction: row;
   aling-items: center;
   justify-content: center;
`

export default function Card(props) {
   return (
      <cardStyle>
         <button onClick={props.onClose}>X</button>
         <h2>{props.name}</h2>
         <h2>{props.status}</h2>
         <h2>{props.species}</h2>
         <h2>{props.gender}</h2>
         <h2>{props.origin}</h2>
         <img src={props.image} alt='' />
      </cardStyle>
   );
}
