import { Link } from 'react-router-dom'; 
import style from 'styled-components';
import { styleJs } from './styles';
import './styles.css';
import styleModule from './styles.module.css';
import React, { useState } from 'react';

const OriginItem = style.div`
   color: white;
   font-family: Rick2;
   font-size: 18px;
   text-shadow: 5px 5px 5px black;
   padding-bottom: 20px;
`

export default function Card(props) {
   const [isHovered, setIsHovered] = useState(false);

   const Card = (props) => {
      const { id, name } = props.characters;
   }

   return (
      <div 
         style={{
            ...styleJs.containerCard,
            ...(isHovered && styleJs.containerCardHover),
         }}
         onMouseEnter={() => setIsHovered(true)}
         onMouseLeave={() => setIsHovered(false)}
         >
         <button 
            onClick={() => props.onClose(props.id)}
            className='styleButton'
         >X</button>
         <img 
            src={props.image} 
            alt='No se puedo cargar la imagen' 
            width={180} 
            height={180}
            style={styleJs.stylesImg}
         />
         <Link to={`/detail/${id}`}>
            <h2 style={styleJs.title}>{props.name}</h2>
         </Link>
         <div 
            className={styleModule.containerSubtitle}
         >
            <h2>{props.species}</h2>
            <h2>{props.gender}</h2>
         </div>
         <OriginItem>{props.origin}</OriginItem>
      </div>
   );
}
