import styled from 'styled-components';

const Container = styled.div`
    background-color: lightcyan;
    border: 2px solid black;
    border-radius: 15px;
    padding: 10px;
    margin: 10px;
    box-shadow: 5px 5px 5px 0px lightgreen;
    float: left;
    position: relative;
`;

const Button = styled.button`
    background-color: red;
    border-radius: 9px;
    border: 1px solid black;
    position: absolute;
    right: 0;
    left: 10;
    top: 0;
    bottom: 20;
`
const Img = styled.img`
    border-radius: 10px;
    position: relative;
    align-items: center;
    padding: 10px
`


export { Container, Button, Img};
